opam.opam:
opam-version: "1.2"
name: "conf-which"
version: "1"
maintainer: "unixjunkie@sdf.org"
authors: "Carlo Wood"
license: "GPL-2+"
homepage: "http://www.gnu.org/software/which/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["which" "which"]
depexts: [
  ["which"] {"centos"}
  ["which"] {"fedora"}
  ["which"] {"opensuse"}
  ["debianutils"] {"debian"}
  ["debianutils"] {"ubuntu"}
  ["which"] {"nixpkgs"}
  ["which"] {"archlinux"}
]

opam-version: "1.2"
name: "conf-perl"
version: "1"
maintainer: "tim@gfxmonk.net"
license: "GPL-1+"
homepage: "https://www.perl.org/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["perl" "--version"]
depexts: [
  ["perl"] {"debian"}
  ["perl"] {"ubuntu"}
  ["perl"] {"alpine"}
  ["perl"] {"nixpkgs"}
  ["perl"] {"archlinux"}
  ["perl-Pod-Html"] {"fedora"}
]

opam-version: "1.2"
name: "conf-m4"
version: "1"
