depexts: [
  ["m4"] {"debian"}
  ["m4"] {"ubuntu"}
  ["m4"] {"fedora"}
  ["m4"] {"rhel"}
  ["m4"] {"centos"}
  ["m4"] {"alpine"}
  ["m4"] {"nixpkgs"}
  ["m4"] {"opensuse"}
  ["m4"] {"oraclelinux"}
  ["m4"] {"archlinux"}
]
